  retrieveWelcomeMessage() {
    HelloWorldService.executeHelloWorldService(this.state.userName).then(response => this.handleSuccessfulResponse(response)).catch(error => this.handleError(error));
  }

  retrieveHealthStatus() {
    UserDataService.retrieveUser(this.state.userId).then(response => {
      this.setState({
        shnStatus: response.data.shnStatus,
        covidStatus: response.data.covidStatus
      });
    });
  }

  toggleCovidStatus() {
    UserDataService.retrieveUser(this.state.userId).then(response => {
      let user = {
        id: response.data.id,
        email: response.data.email,
        name: response.data.name,
        password: response.data.password,
        shnStatus: response.data.shnStatus,
        covidStatus: !response.data.covidStatus,
        authority: response.data.authority,
        business: response.data.business
      };
      UserDataService.updateUser(user);
    });
    this.setState({
      covidStatus: !this.state.covidStatus
    });
  }

  toggleShnStatus() {
    UserDataService.retrieveUser(this.state.userId).then(response => {
      let user = {
        id: response.data.id,
        email: response.data.email,
        name: response.data.name,
        password: response.data.password,
        shnStatus: !response.data.shnStatus,
        covidStatus: response.data.covidStatus,
        authority: response.data.authority,
        business: response.data.business
      };
      UserDataService.updateUser(user);
    });
    this.setState({
      shnStatus: !this.state.shnStatus
    });
  }

  handleSuccessfulResponse(response) {
    this.setState({
      welcomeMessage: response.data.message
    });
  }

  handleError(error) {
    console.log(error.response);
    let errorMessage = '';

    if (error.message) {
      errorMessage += error.message;
    }

    if (error.response && error.response.data) {
      errorMessage += error.response.data.message;
    }

    this.setState({
      errorMessage: errorMessage
    });
  }

}

export default WelcomeComponent;
